/**
 * This class represents an application a student is trying to fill.
 * @constructor 
 * */
class ExternalApplication {
  constructor(context, slipTitle) {
    if (!context.args) return new Error("No args passed to External Application");
    if (!context.entityRepo) return new Error("Invalid setup , repo cannot be null ");
    this.register = this.register.bind(this);
    this.validate = this.validate.bind(this);
    this.extend = this.extend.bind(this);
    this.context = context;
    this.slipTitle = slipTitle || "Acknowledgement Slip";
  }
  /**
   * Used by the application to validate the object its supposed to store.
   *@param {Function} callback Callback function.
   * */
  validate(callback) {
    setImmediate(callback);
  }
  /**
   * Used by the application to extend the object its supposed to store.
   *@param {Function} callback Callback function.
   * */
  extend(callback) {
    setImmediate(callback);
  }
  /**
   * 
   * Used to submit an application.
   * @param {String} applicationClass This is the propertyName of the application on the domain object. It is used to retrieve the collection to store the application.
   * @param {Array} templates This is used to fetch embeddable html templates that have been mapped in ExternalApplication.MAP.
   * @param {Array} extensions These are additional html files to include in the final template. i.e [['HTML_TEMPLATE_NAME',{optional_context}]]
   * @param {Function} callback Callback function.
   * */
  register(applicationClass, templates, extensions, callback) {
    this.context.debug("registering a new applicant");
    this.context.debug("applicant invoiceNumber:" + this.context.args.invoiceNumber);
    this.context.libs.parseObjectIds.call(this.context, this.context.args);
    this.context.debug("parsed object ids");
    this.context.debug("current args:");
    this.context.debug(JSON.stringify(this.context.args, null, " "));
    let _domain,
      reversal = false;

    this.context.async.waterfall(
      [
        this.context.libs.getDomain.bind(this.context),
        (domain, fn) => {
          this.domain = domain;
          this.context.entityRepo.get(
            domain.config[applicationClass],
            { invoiceNumber: this.context.args.invoiceNumber },
            { one: true, full: true },
            (er, application) => {
              if (er) return fn(er);
              fn(null, { domain, application });
            }
          );
        },
        ({ application, domain }, fn) => {
          _domain = domain;
          if (application) return fn(null, { newApp: application, domain, appExists: true });

          this.context.debug("validating form state");
          this.validate(er => {
            if (er) return fn(er);
            this.context.debug("extending model if necessary");
            this.extend(er => {
              if (er) return fn(er);
              this.context.debug(`creating a new application of type ${applicationClass}`);
              this.context.entityRepo.create(domain.config[applicationClass], this.context.args, (er, newApp) => {
                if (er)
                  return this.context.debug(`an error occurred while creating application of type ${applicationClass} error:${er}`), fn(er);

                this.context.debug(`successfully created ${applicationClass}`);
                if (!this.context.args.personalDetails || !this.context.args.personalDetails.passport) return fn(null, { domain, newApp });
                this.context.entityRepo.infrastructure().fileUpload.moveToPermanentSite(this.context.args.personalDetails.passport, er => {
                  if (er)
                    return (
                      this.context.debug("an error occurred while moving passport to permanent site , reversal required"),
                      (reversal = { _id: newApp && newApp._id, entityName: domain.config[applicationClass] }),
                      fn(er)
                    );

                  //populate the app.

                  this.context.entityRepo.get(
                    domain.config[applicationClass],
                    { _id: newApp._id },
                    { full: true, one: true },
                    (er, _newApp) => {
                      if (er) return fn(er);
                      if (!_newApp)
                        return fn(new Error("For some strange reason we cannot find your application. Please contact system admin"));
                      return fn(null, { domain, newApp: _newApp });
                    }
                  );
                });
              });
            });
          });
        },
        ({ domain, newApp, appExists }, fn) => {
          if (appExists) return fn(null, newApp);
          this.context.debug(`retrieving ${applicationClass} ${newApp}`);
          this.context.debug(JSON.stringify(newApp));
          this.context.entityRepo.get(domain.config[applicationClass], { _id: newApp._id }, { one: true, full: true }, fn);
        },
        (application, fn) => {
          if (!application)
            return (
              this.context.debug(`failed to retrieve ${applicationClass}`),
              fn(new Error("Could not find any application with that payment reference"))
            );
          fn(null, application);
        },
        (application, fn) => {
          this.context.debug("about to get templates");
          try {
            Object.defineProperties(this.context.args, { $appDomain: { enumerable: false, value: this.domain } });
          } catch (e) {}
          this.context.async.parallel(
            (templates || [])
              .map(x => {
                return _callback => {
                  this.context.debug(`calling mapped function ${x} , value in map ${ExternalApplication.MAP[x]}`);
                  this.context.libs[ExternalApplication.MAP[x]].call(this.context, application, _callback);
                };
              })
              .concat(
                (extensions || []).map(x => {
                  return _callback => {
                    this.context.debug("calling extension ..");

                    x.call(this.context, application, _callback);
                  };
                })
              ),
            (er, _templates) => {
              if (er) return this.context.debug(`an error occurred during the template gathering ${er}`), fn(er);
              return fn(
                null,
                _templates.reduce((sum, v) => {
                  return sum.concat(v);
                }, [])
              );
            }
          );
        },
        (_templates, fn) => {
          this.context.debug("got templates");
          //this.context.debug(_templates);
          this.context.libs.getHtmlTemplates.call(this.context, [["GENERAL_STYLES"], ..._templates], _domain._id, (er, sections) => {
            if (er) return fn(er);
            fn(null, { sections, styles: [] });
          });
        },
        (data, fn) => {
          this.context.libs.getHtml.call(this.context, "SECTION_TEMPLATE", data, _domain._id, fn);
        }
      ],
      (er, html) => {
        if (reversal) return this.context.debug("calling reverse method"), ExternalApplication.reversal.call(this, reversal, er, callback);

        if (er) return this.context.debug("an error occurred during registeration returning error"), callback(er);

        this.context.args.$nextStep.description = this.slipTitle;
        return callback(null, { html });
      }
    );
  }
  getInfo(applicationClass, query, callback) {
    if (Array.prototype.slice.call(arguments).length == 2) {
      callback = query;
      query = null;
    }
    this.context.entityRepo.get(this.context.args.$appDomain.config[applicationClass], query, callback);
  }
}

//mapped partials.
ExternalApplication.MAP = {
  PERSONAL_DETAILS: "preparePassportAndPersonalDetails",
  OLEVEL_DETAILS: "prepareOlevelDetails",
  JAMB_DETAILS: "prepareJambDetails",
  SHOPPING_DETAILS: "prepareAdmissionShopping",
  CHANGE_OF_DEGREE_DETAILS: "prepareChangeOfDegreeDetails",
  EXECUTIVE_DIPLOMA_DETAILS: "prepareExecutiveDiplomaDetails"
};
ExternalApplication.reversal = function({ _id, entityName }, er, callback) {
  this.context.debug("reversing...");

  this.context.entityRepo.delete(entityName, { _id }, deleteError => {
    if (!deleteError) {
      this.context.debug("successfully deleted application");
      this.context.debug("checking for custom reversal");
      if (this.extendReversal) {
        this.context.debug("has extend reversal");

        return this.extendReversal(_id, entityName, customReversalError => {
          if (customReversalError) {
            this.context.debug("Failed in custom reversal");
            this.context.debug(customReversalError);
          }
          return callback(er);
        });
      }
    } else this.context.debug(deleteError);

    callback(er);
  });
};
exports = ExternalApplication;

