this.entityRepo.get("Department", Object.assign({}, this.args.query || {}, { domain: this.args.$user.domain }), (er, deps) => {
  if (er) return callback(er);
  if (!deps.length) return callback(null, deps);
  callback(
    null,
    deps.map(x => {
      let result = {
        id: x._id,
        displayLabel: x.name,
        elements: []
      };

      if (x.areasOfSpecialization && x.areasOfSpecialization.length)
        result.elements.push(
          this.libs.createElement("areaOfSpecialization", "Area of Specialization", "", this.constants.ELEMENTTYPE.SELECTSET, {
            items: x.areasOfSpecialization.map(a => ({ id: a._id, displayLabel: a.name }))
          })
        );
      return result;
    })
  );
});

