const required = [
  {
    validatorType: "REQUIRED"
  }
];
//debugger;
this.libs.getProcessors(
  this.entityRepo,
  ["GET_SESSIONS", "GET_YearOfStudy_5", "GET_MODEOFSTUDY", "GET_SCHOOLPROGRAMME", "GET_PaymentType_8"],
  (er, processors) => {
    if (er) return callback(er);

    this.entityRepo.get(
      "StudentType",
      {
        domain: this.args.$user.domain
      },
      (er, studentTypes) => {
        if (er) return callback(er);

        let elements = [],
          e = this.libs.createElement.bind(this),
          c = this.constants;

        this.entityRepo.get(
          "FeePurpose",
          {
            domain: this.args.$user.domain
          },
          (er, feePurposes) => {
            if (er) return callback(er);
            const getSession = () =>
              e(
                "session",
                "Session",
                "",
                c.ELEMENTTYPE.SELECT,
                {
                  type: "PROCESSOR",
                  config: {
                    value: processors.GET_SESSIONS
                  }
                },
                required
              );
            let items = feePurposes.map(f =>{
              let _elements = [];
              if (/school\s*fees/gi.test(f.name) || /acceptance/gi.test(f.name)) {
                _elements.push(
                  e(
                    "studentType",
                    "Student Type",
                    "",
                    c.ELEMENTTYPE.SELECTSET,
                    {
                      path: "config",
                      items: studentTypes.map(x => ({
                        displayLabel: x.name,
                        id: x._id.toString(),
                        elements: getFor(x, processors)
                      }))
                    },
                    required
                  )
                );

                _elements.push(getSession());
              }

              return {
                id: f._id,
                displayLabel: f.name,
                elements: _elements
              };
            });

            let feePurpose = e("feePurpose", "Fee Purpose", "", c.ELEMENTTYPE.SELECTSET, {
              path: "criteria",
              items
            });
            elements.push(feePurpose);

            callback(null, elements);
          }
        );
      }
    );
  }
);

const getFor = (x, processors) => {
  let paymentType = this.libs.createElement(
      "paymentType",
      "Payment Type",
      "",
      this.constants.ELEMENTTYPE.SELECT,
      {
        type: "PROCESSOR",
        config: {
          value: processors.GET_PaymentType_8,
          customArgs: JSON.stringify({
            query: {
              group: x.group
            }
          })
        }
      },
      required
    ),
    yearOfStudy = this.libs.createElement(
      "yearOfStudy",
      "Year of Study",
      "",
      this.constants.ELEMENTTYPE.SELECT,
      {
        type: "PROCESSOR",
        config: {
          customArgs: JSON.stringify({ query: { group: "UNDERGRADUATE" } }),
          value: processors.GET_YearOfStudy_5
        }
      },
      required
    );
  if (/pg/i.test(x.group)) {
    yearOfStudy.args.config.customArgs = JSON.stringify({ query: { group: "PG" } });
    return [
      yearOfStudy,
      paymentType,
      this.libs.createElement(
        "programmeOption",
        "Programme Options",
        "",
        this.constants.ELEMENTTYPE.SELECT,
        {
          type: "PROCESSOR",
          config: {
            value: processors.GET_SCHOOLPROGRAMME,
            customArgs: JSON.stringify({
              query: {
                group: x.group
              }
            })
          }
        },
        required
      ),
      this.libs.createElement(
        "forStaff",
        "Staff",
        "",
        this.constants.ELEMENTTYPE.INPUT,
        {
          type: this.constants.INPUTTYPE.CHECKBOX
        },
        required
      ),
      this.libs.createElement(
        "modeOfStudy",
        "Mode of Study",
        "",
        this.constants.ELEMENTTYPE.SELECT,
        {
          type: "PROCESSOR",
          config: {
            value: processors.GET_MODEOFSTUDY
          }
        },
        required
      )
    ];
  }
  if (/undergraduate/i.test(x.group)) {
    return [
      paymentType,
      yearOfStudy,
      this.libs.createElement("yearOfEntry", "Year of Entry", "", this.constants.ELEMENTTYPE.SELECT, {
        type: "PROCESSOR",
        config: {
          value: processors.GET_YearOfStudy_5
        }
      })
    ];
  }

  return [];
};
