//get the uploaded list from storage and loop over the list.
//will need to use a web worker if this is too CPU intensive.

let inf = this.entityRepo.infrastructure(),
  self = this,
  fileUpload = inf.fileUpload,
  fileParser = inf.fileParser,
  threadPool = inf.threadPool;
this.debug("=============================================================");
this.debug(this.args);
this.libs.parseObjectIds.call(this, this.args);
let branches = {
  EXISTING: () => {
    let args = this.args.config,
      studentKind = args.studentKind;

    this.libs.resolveStudentType(studentKind, this.entityRepo, (er, studentType) => {
      if (er) return callback(er);

      if ((studentKind.indexOf("UNDERGRADUATE") !== -1 || studentKind.indexOf("PG") !== -1) && args.studentDetails.departmentOption) {
        args.studentDetails.departmentOption = args.studentDetails.departmentOption.areaOfSpecialization;
      }
      if (studentKind.indexOf("UNDERGRADUATE") !== -1) {
        //arrange the select set options properly.
        studentType = args.studentDetails.studentMode;
        args.studentDetails.modeOfEntry = args.studentDetails.modeOfEntry.value;
      }

      let contx = { studentKind, studentType };
      this.debug(contx);
      try {
        this.libs.convertFileAndSave(
          this.args.$domain.config.existingStudentClass,
          this.args.config.list,
          contx,
          ["firstName", "surname", "middlename", "regNo"],
          (list, fn) => {
            this.async.parallel(list.map(() => this.entityRepo.create.bind(null, studentKind, args.studentDetails)), (er, details) => {
              if (er) return fn(er);
              this.debug("saved everything we need to save.");
              this.debug("will assign and return");
              this.debug(details);
              fn(
                null,
                list.map((x, index) => {
                  return (x.programmeDetails = details[index][0]._id.toString()), x;
                })
              );
            });
          },
          fileUpload,
          fileParser,
          threadPool,
          this.entityRepo,
          this.args.$user,
          (error, result) => {
            if (error) callback(error);
            else callback(null, { info: "Admission list was successfully uploaded" });
          }
        );
      } catch (e) {
        this.debug("synchronous error caught");
        callback(e);
      }
    });
  },
  NEW: () => {
    let args = this.args.config,
      contx = { studentType: args.studentType, programmeDetails: args.programmeDetails };
    this.libs.convertFileAndSave(
      this.args.$domain.config.admissionTicketClass,
      args._list,
      contx,
      ["firstName", "surname", "middlename", "refNo"],
      null,
      fileUpload,
      fileParser,
      threadPool,
      this.entityRepo,
      this.args.$user,
      (error, result) => {
        if (error) callback(error);
        else callback(null, { info: "Admission list was successfully uploaded" });
      }
    );
  }
};

if (!fileUpload || !fileParser || !threadPool) {
  this.debug(`something is undefined ...fileUpload:${!!fileUpload}, fileParser:${!!fileParser}, threadPool:${!!threadPool}`),
    callback(new Error("Infrastructure has a problem"));
} else if (!this.args.config || (!this.args.config.list && !this.args.config._list)) {
  this.debug("user didnt upload any file"), callback(new Error("Missing Student data file"));
} else branches[this.args.studentType]();

//check if this student account has already been created.
//if it has , return the interim username and password.
//if it hasnt create the student account with an interim username and password.
let decisionTree = {
  NEW: (domain, fn) => {
    this.libs.verifyStudent.call(
      this,
      {
        studentEntity: domain.config.studentClass,
        studentEntityKindClass: domain.config.admissionTicketClass,
        studentRole: domain.config.studentRole,
        domainUID: domain.uid
      },
      fn
    );
  },
  EXISTING: (domain, fn) => {
    this.libs.verifyStudent.call(
      this,
      {
        studentEntity: domain.config.studentClass,
        studentEntityKindClass: domain.config.existingStudentClass,
        studentRole: domain.config.studentRole,
        domainUID: domain.uid,
        resolveEntityKind: () => {
          return this.entityRepo.get.bind(
            null,
            domain.config.existingStudentClass,
            { regNo: this.args.option.regNo, studentType: this.args.studentType },
            { full: true }
          );
        },
        extendNewStudent: () => ({ regNo: this.args.option.regNo, cleared: true })
      },
      fn
    );
  }
};

this.libs.getDomain((er, domain) => {
  if (er) return callback(er);
  if (!domain) return callback(new Error("Something is horribly wrong with our configuration. Please contact system administrator"));
  decisionTree[this.args.studentOption](domain, (error, username) => {
    callback(null, {
      info: `Welcome! Your username is ${username} and password is ${username}.\nYou are advised to login, change your password and update your profile.`
    });
  });
});
